import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import NestedModal from './components/popup.js';
import SignUp from './components/SignUp.js';
import Footer from './components/footer.js';
import PrimarySearchAppBar from './components/header.js';

function MyButton() {
  const [count, setCount] = useState(0);
  const [titl, setTitl] = useState("Starter");
  function handleClick() {
    setCount(count+1);
    level(count);
  }
  function level(count)
  {
    if(count>=1000)
      setTitl("MASTER CLICKER");
    else if(count>=500)
      setTitl("Experienced Clicker");
    else if(count>=250)
      setTitl("Regular Clicker");
    else if(count>=100)
      setTitl("Basic Clicker");
    else if(count>=25)
      setTitl("Apprentice Clicker");




    document.getElementById('Say').titl=titl;
  }


  return (
    <>
    <p id='Say'>{titl} clicked {count} times</p>
    <button onClick={handleClick}>I'm a button</button>
    </>

  );
}

function LoginPage(){

  return(
    <div className='Butt'>
      <img src={logo} className="App-logo" alt="logo" />
      <p>helpme</p>
      <form>
        <label for="uname">Username : </label>
        <input type='text' id='uname' name='uname' placeholder='PlayerOne'/><br/>
        <label for="psw">Password : </label>

      </form>
    </div>

  );
}


function Testing(){
  return(
    <>
    <p>This is where I begin my villain monologue.</p>
    <h1>But <br /> nahhh</h1>
    </>
  );
}

function App() {
  return (
    <div className="App">
      <PrimarySearchAppBar />
      <div className='App-body'>
        <SignUp />

        </div>

      <body className="App-Testing">
        <p>HELLO</p>
        <MyButton />
        <NestedModal />
        <Testing />

      </body>
      <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
        <p>bruh</p>
      </header>
      <footer className='App-footer'>
        <Footer />
      </footer>

    </div>


  );
}

export default App;
