import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function MyButton() {
  const [count, setCount] = useState(0);
  function handleClick() {
    setCount(count+1);
  }
  return (
    <>
    <button onClick={handleClick}>I'm a button</button>
    <p>Big man clicked {count} times</p>
    </>

  );
}

function LoginPage(){

  return(
    <form action="action_page.php" method="post">
    <div class="imgcontainer">
      <img src="img_avatar2.png" alt="Avatar" class="avatar"></img>
    </div>

    <div class="container">
      <label for="uname"><b>Username</b></label>
      <input type="text" placeholder="Enter Username" name="uname" required></input>

      <label for="psw"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" name="psw" required></input>

      <button type="submit">Login</button>
      <label>
        <input type="checkbox" checked="checked" name="remember"> Remember me </input>
      </label>
    </div>

    <div class="container" >
      <button type="button" class="cancelbtn">Cancel</button>
      <span class="psw">Forgot <a button>password?</a></span>
    </div>
  </form>

  );
}


function Testing(){
  return(
    <>
    <p>This is where I begin my villain monologue.</p>
    <h1>But <br /> nahhh</h1>
    </>
  );
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <div className='Butt'>TEST</div>
      </header>
      <body className="App-Testing">
        <p>HELLO</p>
        <MyButton />
        <Testing />
        <LoginPage />
      </body>
    </div>
  );
}

export default App;
